import Header from '../../components/header';
import { Container, Body } from '../zstylesGlobal/styles';
import AuthGoogle from '../../services/Api/index'
import cake from '../../assets/Imag/cake2.gif'
import { ContainerRoot } from './styles';

export const Login = () => {

  return (
    <ContainerRoot>
    <Container>
      <Header />
      <Body>
        <div className='container-login'>
          <div className='wrap-logon'>
            <form className='login-form'>

              <span className='login-form-title'>Bem Vindo</span>

              <span className='login-form-title'>
                <img src={cake} alt="CakeNiver" />
              </span>
              <div className='wrap-input'>
                <input type='email' />
                <span className='focus-input' data-placeholder='Email'></span>
              </div>

              <div className='wrap-input'>
                <input type='password' />
                <span className='focus-input' data-placeholder='Password'></span>
              </div>
              <div className='container-login-form-btn'>
                <button className="login-form-btn">Login</button>
              </div>

              <div className="text-center-criarConta"></div>
              <spn className="txt1">Não possui conta?</spn>
              <a href="#" className="txt2">Criar Conta</a>
            </form>
            <AuthGoogle />
          </div>
        </div>
        
      
      </Body>
    </Container>
      </ContainerRoot>
  )
}

