import { collection, getDocs, getFirestore } from "firebase/firestore"
import { useState, useEffect } from "react"
import firebaseConfig from "../../services/config/firebase";
import { Container, ContainerSemana, Semanas,TileSemana } from './styles'
// https://www.youtube.com/watch?v=gqbXnYhvB5E&t=264s

const ListUsers = () => {

  const [users, setUsers] = useState([])
  const db = getFirestore(firebaseConfig)
  const usersCollectionRef = collection(db, "users")

  // trás todos os dados que estão cadastrados no banco
  useEffect(() => {
    const getUsers = async () => {
      const data = await getDocs(usersCollectionRef)
      const usersCadastrados = (data.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
      setUsers(usersCadastrados)
    }
    getUsers()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
    <Container>
      <ContainerSemana>
        <Semanas>
          <TileSemana>Segunda-feira</TileSemana>
          <Title></Title>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name}</li>
                </div>
              )
            })}
            {users.map(users => {
              return (
                <div>
                  <li>{users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>
        <Semanas>
          <ul>
            {users.map(users => {
              return (
                <div>
                  <li>{users.name} {users.birthDate}</li>
                </div>
              )
            })}
          </ul>
        </Semanas>

      </ContainerSemana>
    </Container>
  )
}
export default ListUsers
