  padding: 1.2em 3.6em 1.2em 3.6em;
  text-transform: uppercase;
  font-family: 'Montserrat', sans-serif;
  font-weight: 900;
}
//Mudao texto que está dentro do botão ao passar o mouse
.btn:hover, .btn:focus { 
  color: #fff;
  outline: 0.05;
}

.fourth {
  border-color: #fff;
  color: #fff;
  font-size:145%;
  box-shadow: 0 0 40px 40px #b3b078d5  inset, 0 0 0 0 #b3b078d5;
  transition: all 150ms ease-in-out;  
}
.fourth:hover {
  box-shadow: 0 0 10px 0 #b3b078d5 inset, 0 0 10px 4px #b3b078d5;
}*/
`

export {
  StylesTile
}


