            </div>
          )
        })}
      </ul>
        </Semanas>
        <Semanas>
        <ul>
        {users.map(users => {
          return (
            <div>
              <li>{users.name} {users.birthDate}</li>
            </div>
          )
        })}
      </ul>
        </Semanas>
        <Semanas>
        <ul>
        {users.map(users => {
          return (
            <div>
              <li>{users.name} {users.birthDate}</li>
            </div>
          )
        })}
      </ul>
        </Semanas>
        <Semanas>
        <ul>
        {users.map(users => {
          return (
            <div>
              <li>{users.name} {users.birthDate}</li>
            </div>
          )
        })}
      </ul>
        </Semanas>
