import Header from '../../components/header';
import { Body, Container } from '../ZStyles';


export const Login = () => {


  return(
    <Container>     
    <Header /> 
    <Body>   
      <button>
        Logar Com Google
      </button>

    </Body>
    </Container>
  )
}


