import { collection, getDocs, getFirestore } from "firebase/firestore"
import { useState, useEffect } from "react"
import firebaseConfig from "../../services/config/firebase";
import { Container, ContainerTop10, ListTop10, TitleTop10, SubTitles, HR, Data, BesideInputContainer, Email, WhatsApp } from './styles'
// https://www.youtube.com/watch?v=gqbXnYhvB5E&t=264s

const ListTOP10 = () => {

  const [users, setUsers] = useState([])
  const db = getFirestore(firebaseConfig)
  const usersCollectionRef = collection(db, "users")

  // trás todos os dados que estão cadastrados no banco
  useEffect(() => {
    const getUsers = async () => {
      const data = await getDocs(usersCollectionRef)
      const usersCadastrados = (data.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
      setUsers(usersCadastrados)
    }
    getUsers()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
    <Container>
      <ContainerTop10>
        <ListTop10>
          <TitleTop10>OS PRÓXIMOS 10 ANIVERSARIANTES DO MÊS</TitleTop10>
          <BesideInputContainer>    
            <SubTitles>
            <p className="Name">Nome</p>
              </SubTitles>      
            <p className="birthdate">Data Nasc.</p>
            <SubTitles>
              </SubTitles>      
            <SubTitles>
              </SubTitles>      
            <SubTitles>
              </SubTitles>      
            <p className="birthdate">Email</p>
            <p className="birthdate">WhatsApp.</p> 
          </BesideInputContainer>

          <HR> ______________________________________________________________________________________________________ </HR>
         <BesideInputContainer>
          <Data>
            <div className="ContainerName">
              <ul>
                {users.map(users => {
                  return (
                    <li>
                      {users.name}
                    </li>
                  )
                })}
              </ul>
            </div>
            <div className="ContainerBirthdate">
              <ul>
                {users.map(users => {
                  return (
                    <li>
                      {users.birthDate}
                    </li>
                  )
                })}
              </ul>
            </div>
          </Data>
          <Email>email</Email>
          <WhatsApp>whatsApp</WhatsApp>
          </BesideInputContainer>
        </ListTop10>
      </ContainerTop10>
    </Container>
  )
}
export default ListTOP10
