import styled from "styled-components";

const StylesTile = styled.div`
  display: flex;
  cursor: none;

  .btn{
    border-top: 1px solid #f7f6ef;
   background: #dfe4e8;
   background: -webkit-gradient(linear, left top, left bottom, from(#d9a815), to(#dfe4e8));
   background: -webkit-linear-gradient(top, #d9a815, #dfe4e8);
   background: -moz-linear-gradient(top, #d9a815, #dfe4e8);
   background: -ms-linear-gradient(top, #d9a815, #dfe4e8);
   background: -o-linear-gradient(top, #d9a815, #dfe4e8);
   padding: 16.5px 33px;
   -webkit-border-radius: 8px;
   -moz-border-radius: 8px;
   border-radius: 8px;
   -webkit-box-shadow: rgba(0,0,0,1) 0 1px 0;
   -moz-box-shadow: rgba(0,0,0,1) 0 1px 0;
   box-shadow: rgba(0,0,0,1) 0 1px 0;
   text-shadow: rgba(0,0,0,.4) 0 1px 0;
   color: white;
   font-size: 24px;
   font-family: Helvetica, Arial, Sans-Serif;
   text-decoration: none;
   vertical-align: middle;
   }
.btn:hover {
   border-top-color: #cca83b;
   background: #cca83b;
   color: #cccccc;
   }
.n:active {
   border-top-color: #c8cfd4;
   background: #c8cfd4;
   }



