import styled from 'styled-components'


const WrapButtonAnel = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  `

const StylesButtonAnel = styled.button`
  min-width: 600px;
  min-height: 60px;
  font-family: 'Nunito', sans-serif;
  font-size: 30px;
  text-transform: uppercase;
  letter-spacing: 1.3px;
  font-weight: 700;
  color: #313133; // fonte
  background: #af72dd;
  background: linear-gradient(90deg, #af72dd 0%, #af72dd 100%);
  border: none;
  border-radius: 1000px;
  box-shadow: 12px 12px 24px #af72dd;
  transition: all 0.3s ease-in-out 0s;
  cursor: pointer;
  outline: none;
  position: relative;
  padding: 10px;

&:hover, &:focus {
  color: #af72dd;
  transform: translateY(-6px);
}

&:hover::before, &:focus::before {
  opacity: 1;
}

::after {
  content: '';
  width: 60px; height: 30px;
  border-radius: 100%;
  border: 3px solid #af72dd;
  position: absolute;
  z-index: -1;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: ring 1.5s infinite;
}

&::hover::after, &::focus::after {
  animation: none;
  display: none;
}

@keyframes ring {
  0% {
    width: 600px;
    height: 60px;
    opacity: 1;
  }
  100% {
    width: 300px;
    height: 300px;
    opacity: 0;
  }
}
`

export {
  WrapButtonAnel,
  StylesButtonAnel
}
