import styled from "styled-components";

const HeaderContainer = styled.div`
    padding: 0;
    margin: 0;
    box-sizing: border-box;
`
    const ContainerUp =styled.div`    
    width: 100%;
    height: 70px;
    display: flex;
    overflow: hidden;
    justify-content: space-between; 
    align-items: center;
    padding: 10px;
 
    //background gradiente
    background: -webkit-linear-gradient(to left, #b721ff, #333);
    background: -o-linear-gradient(to left, #b721ff, #333);
    background: -moz-linear-gradient(to left, #b721ff, #333);
    background: linear-gradient(to left, #b721ff, #333);

    box-shadow: 0px 5px 20px #000;
    `
  const Title =styled.div`
  color: #fff;
  text-shadow:0px 0px 3px #f05be6;
  font-size: 30px;

  @media screen and (max-width: 600px) {
    font-size: 20px;
}
  @media screen and (max-width: 320px) {
    font-size: 10px;
}
  `
  const Date =styled.div`
  display: flex;
  justify-content: center;

@media screen and (max-width: 600px) {
  width: 70px;
}
  @media screen and (max-width: 320px) {
    width: 50px;
}
  `

const ContainerMenu = styled.div`
  width: 75%;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  margin: 0.5% 0 1% 4%;
  gap: 0.2rem;
`
export {
  HeaderContainer,
  ContainerMenu,
  Title,
  Date,
  ContainerUp

}
